import React from 'react';
import { GridComponent, ColumnsDirective, ColumnDirective } from '@syncfusion/ej2-react-grids';

function DailyTasks({ currentInfo, tasks, tasksDoneToday, weeklyData, onToggleTask }) {
  // 显示当前日期、公历 & 农历、天气、运势
  const { date, lunar, weather, fortune } = currentInfo;

  // 根据任务列表构造 Syncfusion Grid 的列配置
  const taskColumns = tasks.map(task => ({
    field: `task_${task.id}`,
    headerText: task.name,
    type: 'boolean',
    displayAsCheckBox: true,
    textAlign: 'Center',
    width: '120'
  }));

  return (
    <div>
      {/* 顶部当前信息栏 */}
      <div style={{ marginBottom: '15px' }}>
        <div><strong>日期：</strong>{date}（{new Date(date).toLocaleDateString('zh-CN', { weekday: 'long' })}）</div>
        <div><strong>农历：</strong>{lunar}</div>
        <div><strong>天气：</strong>{weather}</div>
        <div><strong>运势：</strong>{fortune}</div>
      </div>

      {/* 每日任务列表 */}
      <div style={{ marginBottom: '20px' }}>
        <h4>今日任务打卡：</h4>
        {tasks.length === 0 ? (
          <p>暂无任务，请添加新任务。</p>
        ) : (
          <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
            {tasks.map(task => {
              const checked = tasksDoneToday.includes(task.id);
              return (
                <li key={task.id}>
                  <label>
                    <input 
                      type="checkbox" 
                      checked={checked} 
                      onChange={(e) => onToggleTask(task.id, e.target.checked)} 
                    />
                    {task.name}
                  </label>
                </li>
              );
            })}
          </ul>
        )}
      </div>

      {/* 周度任务汇总表格 */}
      <div>
        <h4>本周任务完成情况：</h4>
        {weeklyData && weeklyData.length > 0 ? (
          <GridComponent dataSource={weeklyData} allowSelection={false} height={300}>
            <ColumnsDirective>
              <ColumnDirective field='day' headerText='星期' textAlign='Center' width='100' />
              {/* 动态生成每个任务列 */}
              {taskColumns.map(col => (
                <ColumnDirective key={col.field} {...col} />
              ))}
            </ColumnsDirective>
          </GridComponent>
        ) : (
          <p>暂无周度数据。</p>
        )}
      </div>
    </div>
  );
}

export default DailyTasks;
