import React, { useState } from 'react';

export default function AddTask({ onTaskAdded }) {
  const [name, setName] = useState('');

  const submit = () => {
    if (!name.trim()) return;
    onTaskAdded(name.trim());
    setName('');
  };

  return (
    <div style={{ margin: '15px 0' }}>
      <input
        type="text"
        placeholder="新任务名称"
        value={name}
        onChange={e => setName(e.target.value)}
        style={{ padding: '6px', width: 200 }}
      />
      <button onClick={submit} style={{ marginLeft: 8, padding: '6px 12px' }}>
        添加任务
      </button>
    </div>
  );
}
