import React from 'react';

export default function Stats({ weekly, tasks }) {
  // 坚持了多少天：本周（或已有数据的天数）里，至少完成一个任务的日子数
  const daysWithAny = weekly.filter(day =>
    tasks.some(t => day[`task_${t.id}`])
  ).length;
  // 总共做了多少项任务：本周所有打卡记录总和
  const totalDone = weekly.reduce((sum, day) =>
    sum + tasks.reduce((s, t) =>
      s + (day[`task_${t.id}`] ? 1 : 0)
    , 0)
  , 0);

  return (
    <div>
      <h3>记录</h3>
      <p style={{ fontSize:18 }}>
        真厉害，你坚持了 <strong>{daysWithAny}</strong> 天！<br/>
        做到了 <strong>{totalDone}</strong> 项任务。
      </p>
    </div>
  );
}
