import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Archive() {
  const [archives, setArchives]     = useState([]);
  const [selectedWeek, setSelected] = useState(null);
  const [weeklyData, setWeekly]     = useState([]);
  const [tasks, setTasks]           = useState([]);

  // 加载档案列表与任务
  useEffect(() => {
    axios.get('http://localhost:5000/api/weekly/archives')
      .then(res => {
        setArchives(res.data.archives);
        if (res.data.archives.length) {
          setSelected(res.data.archives[0]);
        }
      });
    axios.get('http://localhost:5000/api/tasks')
      .then(res => setTasks(res.data.tasks));
  }, []);

  // 加载所选周数据
  useEffect(() => {
    if (!selectedWeek) return;
    axios.get(`http://localhost:5000/api/weekly_records?week_start=${selectedWeek}`)
      .then(res => setWeekly(res.data.weekly));
  }, [selectedWeek]);

  return (
    <div>
      <h3>历史周报档案</h3>
      <div style={{ marginBottom:16 }}>
        {archives.map(week=>(
          <button
            key={week}
            onClick={()=>setSelected(week)}
            style={{
              marginRight:8, padding:'4px 8px',
              background: week===selectedWeek?'#1890ff':'#eee',
              color:    week===selectedWeek?'#fff':'#000',
              border: 'none', cursor:'pointer'
            }}
          >{week}</button>
        ))}
      </div>

      {weeklyData.length>0 ? (
        <table border="1" cellPadding="6"
               style={{ borderCollapse:'collapse', width:'100%' }}>
          <thead>
            <tr style={{ background:'#f4f4f4' }}>
              <th>星期</th>
              {tasks.map(t=> <th key={t.id}>{t.name}</th>)}
            </tr>
          </thead>
          <tbody>
            {weeklyData.map(row=>(
              <tr key={row.date}>
                <td>{row.day}</td>
                {tasks.map(t=>(
                  <td key={t.id} style={{ textAlign:'center' }}>
                    {row[`task_${t.id}`] ? '✓' : ''}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>该周暂无数据。</p>
      )}
    </div>
  );
}
