import React, { useEffect, useState } from 'react';
import axios from 'axios';

import DailyTasks from './components/DailyTasks';
import AddTask     from './components/AddTask';
import Stats       from './components/Stats';
import Archive     from './components/Archive';

function App() {
  const [tasks, setTasks]         = useState([]);
  const [doneToday, setDoneToday] = useState([]);
  const [weekly, setWeekly]       = useState([]);
  const [info, setInfo]           = useState({ date:'', lunar:'', weather:'', fortune:'' });
  const [page, setPage]           = useState('daily');

  const loadAll = async () => {
    try {
      const infoRes = await axios.get('http://localhost:5000/api/current_info');
      const tasksRes= await axios.get('http://localhost:5000/api/tasks');
      const today   = infoRes.data.date;
      const dailyRes= await axios.get(`http://localhost:5000/api/daily_records?date=${today}`);
      const weekRes = await axios.get('http://localhost:5000/api/weekly_records');

      setInfo(infoRes.data);
      setTasks(tasksRes.data.tasks);
      setDoneToday(dailyRes.data.done_tasks);
      setWeekly(weekRes.data.weekly);
    } catch(err) {
      console.error(err);
    }
  };

  useEffect(() => { loadAll(); }, []);

  const toggleTask = async (tid, done) => {
    await axios.post('http://localhost:5000/api/daily_records', {
      date: info.date, task_id: tid, done
    });
    setDoneToday(dt => done ? [...dt, tid] : dt.filter(id=>id!==tid));
    const wk = await axios.get('http://localhost:5000/api/weekly_records');
    setWeekly(wk.data.weekly);
  };

  const addTask = async name => {
    await axios.post('http://localhost:5000/api/tasks', { name });
    const tRes = await axios.get('http://localhost:5000/api/tasks');
    setTasks(tRes.data.tasks);
    const wk = await axios.get('http://localhost:5000/api/weekly_records');
    setWeekly(wk.data.weekly);
  };

  return (
    <div style={{ display:'flex', height:'100vh', fontFamily:'sans-serif' }}>
      {/* 侧边栏 */}
      <nav style={{
        width: 120, borderRight:'1px solid #ddd', padding:'20px', boxSizing:'border-box'
      }}>
        {[
          ['daily','每日打卡'],
          ['add','添加任务'],
          ['stats','记录'],
          ['archive','档案']
        ].map(([key,label])=>(
          <div key={key}
               onClick={()=>setPage(key)}
               style={{
                 marginBottom:12,
                 cursor:'pointer',
                 color: page===key?'blue':'black'
               }}>
            {label}
          </div>
        ))}
      </nav>

      {/* 主区 */}
      <main style={{ flex:1, padding:'20px', overflowY:'auto' }}>
        <h2>每日任务打卡 MVP</h2>
        <div style={{
          marginBottom:16, borderBottom:'1px solid #ccc', paddingBottom:8
        }}>
          <div><strong>日期：</strong>{info.date}</div>
          <div><strong>农历：</strong>{info.lunar}</div>
          <div><strong>天气：</strong>{info.weather}</div>
          <div><strong>运势：</strong>{info.fortune}</div>
        </div>

        {page === 'daily'   && <DailyTasks tasks={tasks} doneToday={doneToday} weekly={weekly} onToggle={toggleTask} />}
        {page === 'add'     && <AddTask    onTaskAdded={addTask} />}
        {page === 'stats'   && <Stats      weekly={weekly} tasks={tasks} />}
        {page === 'archive' && <Archive />}
      </main>
    </div>
  );
}

export default App;
