import React, { useEffect, useState } from 'react';
import axios from 'axios';

import DailyTasks from './components/DailyTasks';
import AddTask from './components/AddTask';
import Stats from './components/Stats';
import Archive from './components/Archive';

function App() {
  const [tasks, setTasks]         = useState([]);
  const [doneToday, setDoneToday] = useState([]);
  const [weekly, setWeekly]       = useState([]);
  const [info, setInfo]           = useState({ date:'', lunar:'', weather:'', fortune:'' });
  const [page, setPage]           = useState('daily');

  // 用于加载所有数据（info, tasks, daily, weekly）
  const loadAll = async () => {
    try {
      const infoRes = await axios.get('/api/current_info');
      const tasksRes= await axios.get('/api/tasks');
      const today   = infoRes.data.date;
      const dailyRes= await axios.get(`/api/daily_records?date=${today}`);
      const weekRes = await axios.get('/api/weekly_records');

      setInfo(infoRes.data);
      setTasks(tasksRes.data.tasks);
      setDoneToday(dailyRes.data.done_tasks);
      setWeekly(weekRes.data.weekly);
    } catch (err) {
      console.error('加载数据失败', err);
    }
  };

  useEffect(() => { loadAll(); }, []);

  // 切换打卡状态
  const toggleTask = async (taskId, done) => {
    try {
      await axios.post('/api/daily_records', {
        date: info.date,
        task_id: taskId,
        done
      });
      setDoneToday(dt => done ? [...dt, taskId] : dt.filter(id => id !== taskId));
      const wk = await axios.get('/api/weekly_records');
      setWeekly(wk.data.weekly);
    } catch (err) {
      console.error('更新打卡失败', err);
    }
  };

  // 添加新任务后刷新列表
  const addTask = async (name) => {
    try {
      await axios.post('/api/tasks', { name });
      await loadAll();
    } catch (err) {
      console.error('添加任务失败', err);
    }
  };

  return (
    <div style={{ display:'flex', height:'100vh', fontFamily:'sans-serif' }}>
      {/* 侧边栏 */}
      <nav style={{
        width: 120,
        borderRight: '1px solid #ddd',
        padding: '20px',
        boxSizing: 'border-box'
      }}>
        {[
          ['daily','每日打卡'],
          ['add','任务管理'],
          ['stats','记录'],
          ['archive','档案'],
        ].map(([key,label]) => (
          <div
            key={key}
            onClick={() => setPage(key)}
            style={{
              marginBottom: 12,
              cursor: 'pointer',
              color: page === key ? 'blue' : 'black'
            }}
          >{label}</div>
        ))}
      </nav>

      {/* 主内容区 */}
      <main style={{ flex:1, padding:'20px', overflowY:'auto' }}>
        <h2>每日任务打卡 MVP</h2>
        <div style={{ marginBottom:16, borderBottom:'1px solid #ccc', paddingBottom:8 }}>
          <div><strong>日期：</strong>{info.date}</div>
          <div><strong>农历：</strong>{info.lunar}</div>
          <div><strong>天气：</strong>{info.weather}</div>
          <div><strong>运势：</strong>{info.fortune}</div>
        </div>

        {page === 'daily' && (
          <DailyTasks
            tasks={tasks}
            doneToday={doneToday}
            weekly={weekly}
            onToggle={toggleTask}
          />
        )}

        {page === 'add' && (
          <AddTask
            tasks={tasks}
            onTasksReload={loadAll}
          />
        )}

        {page === 'stats' && (
          <Stats
            weekly={weekly}
            tasks={tasks}
          />
        )}

        {page === 'archive' && <Archive />}
      </main>
    </div>
  );
}

export default App;
